      if (loggedInUser) {
        const foundUser = loggedInUser;
        setUser(foundUser);
      }
    }, []);


    var getUser = () => {
      axios({
        method : 'post',
        url : 'http://localhost:5000/api/books',
        data : {Email: user}
      })
      .then( function(response) {
          console.log(response)
          setbooks(response.data);
          console.log(books)
          history.push("/list", response.data)
         
