import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import CardComp from '../Components/CardComp'
import NavbarComp from '../Components/NavbarComp'
import {Container, Row, Col} from 'react-bootstrap'

const axios = require('axios')
const center = {
  display: "grid",
  placeItems : "center",
}
const card = {
  boxShadow : "0px 0px 5px #000",
  borderRadius : "5px",
  padding : "20px 80px 20px 80px",
  margin : "10px"
}
const item = {
  padding: " 2px 8px"
}
function Books(props) {
    var [books, setbooks] = useState([])
    var [usersBooks, setUsersBooks] = useState([])
    var [user, setUser] = useState('')
    var history = useHistory()

    //Getting the Current User that has logged in
    useEffect(() => {
      const loggedInUser = localStorage.getItem('Email');
      if (loggedInUser) {
        const foundUser = loggedInUser;
        setUser(foundUser);
      }
    }, []);


    var getUser = () => {
      axios({
        method : 'post',
        url : 'http://localhost:5000/api/books',
        data : {Email: user}
      })
      .then( function(response) {
          console.log(response)
          setbooks(response.data);
          console.log(books)
         
      })
      .catch(function (error) {
          console.log(error);
        })
  }
  var displayUser = () => {
    return <div>{books.map(ele => (
            
            
            <li key = {ele.Isbn}> 
            <Container>
              <Row>
                <Col md ={12}>
                  <CardComp Name = {ele.Name} Author = {ele.Author} Total = {ele.Total} Fun = {() => addBooks(ele.Isbn)} />
                </Col>
              </Row>
            </Container>
{/*             
                  <p style = {item}>{ele.Name}</p>
                  <p style = {item}>{ele.Author}</p>
                  <p style = {item}>{ele.Total}</p> 
                  <p style = {item}>{ele.Isbn}</p>  */}
                  {/* <button onClick = { () => addBooks(ele.Isbn)} >Add</button> */}
            </li>
          ))}

        </div>
}
  var addBooks = (ele) => {
    axios({
      method: 'post',
      url: 'http://localhost:5000/api/borrow',
      data: {Email: user, Isbn: ele}
    })
    .catch(err => console.log(err))
    .then( (response) => {
      alert(response.data)
      history.push("/books")
    })
  }
  var getUsersBooks = () => {
    axios({
      method: 'post',
      url: 'http://localhost:5000/api/displayBooks',
      data: {Email: user}
    })
    .catch(err => console.log(err))
    .then((response) => {
      console.log(response)
      setUsersBooks(response.data)
    })

  }
  var displayUsersBooks = () => {
    return <div style = {center}>{usersBooks.map(ele => (
      <li key = {ele.Isbn} style = {card}> 
            <p style = {item}>{ele.Name}</p>
            <p style = {item}>{ele.Author}</p>
            <p style = {item}>{ele.Isbn}</p> 
      </li>
    ))}

  </div>
  }
    
    return <>
    <NavbarComp />
    <div style = {center}>
    <button onClick = {getUser}>Books</button>
    <div>{displayUser()}</div>
    <div>{user}</div>
    <button onClick = {getUsersBooks}>Users Books</button>
    <div>{displayUsersBooks()}</div>
   
    </div>
    </>
}

export default Books
