import React, {useState} from 'react'

const axios = require('axios')
const center = {
  display: "grid",
  placeItems : "center",
}
const card = {
  boxShadow : "0px 0px 10px #000",
  borderRadius : "20px",
  padding : "20px 80px 20px 80px",
  margin : "10px"
}
const item = {
  padding: "8px"
}
function Users() {
    var [users, setUsers] = useState([])
    
    var getUser = () => {
        axios.get('http://localhost:5000/api/dummy')
        .then( function(response) {
            console.log(response)
            setUsers(response.data);
            console.log(users)


           for(const ele of users) {
               console.log(ele.Name + " " + ele.ID  + " " + ele.Address)
           }


           
        })
        .catch(function (error) {
            console.log(error);
          })
    }
    var displayUser = () => {
        return <div style = {center}>{users.map(ele => (
                <div style = {card}> 
                      <p style = {item}>{ele.Name}</p>
                      <p style = {item}>{ele.ID}</p>
                      <p style = {item}>{ele.Address}</p>
                </div>
              ))}
            </div>

    }
    return <div style = {center}>
    <button onClick = {getUser}>Get All Users</button>
    <div>{displayUser()}</div>
   
    </div>
}

export default Users
