import React, {useState} from 'react'
import NavbarComp from '../Components/NavbarComp'

const axios = require('axios')
const center = {
  display: "grid",
  placeItems : "center",
}
const card = {
  boxShadow : "0px 0px 10px #000",
  borderRadius : "30px",
  padding : "20px 80px 20px 80px",
  margin : "10px"
}
const item = {
  padding: " 2px 8px"
}
function Books() {
    var [users, setUsers] = useState([])
    
    var getUser = () => {
        axios.get('http://localhost:5000/api/books')
        .then( function(response) {
            console.log(response)
            setUsers(response.data);
            console.log(users)


           for(const ele of users) {
               console.log(ele.Name + " " + ele.ID  + " " + ele.Address)
           }


           
        })
        .catch(function (error) {
            console.log(error);
          })
    }
    var displayUser = () => {
        return <div style = {center}>{users.map(ele => (
                <div style = {card}> 
                      <p style = {item}>{ele.Name}</p>
                      <p style = {item}>{ele.Author}</p>
                      <p style = {item}>{ele.Total}</p> 
                      <button>Add</button>
                </div>
              ))}

            </div>

    }
    var addToCart = () => {
      
    }
    return <>
    <NavbarComp />
    <div style = {center}>
    <button onClick = {getUser}>Books</button>
    <div>{displayUser()}</div>
   
    </div>
    </>
}

export default Books
